import React,{useState,useEffect} from 'react'
import { Link, useParams ,useNavigate } from "react-router-dom";
import {ReactComponent as ArrowLeft} from '../assets/arrow-left.svg'
import { browserHistory } from 'react-router'

const NotePage = ({match ,history}) => {
    const params = useParams();
    let noteId = params.id;
    const navigate = useNavigate();

    let [note,setNotes] = useState(null)

    useEffect(() => {
        getNotes()
    },[noteId])

    let getNotes= async() =>{
        let response= await fetch(`/api/notes/${noteId}`)
        let data=await response.json() 
        setNotes(data)
    }


    const updateNote = async () => {
        try {
          await fetch(`/api/notes/${noteId}/update/`, {
            method: 'PUT',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(note),
          });
          // Navigate back to the home page after updating the note
          navigate('/');
        } catch (error) {
          console.error('Error updating note:', error);
        }
      };

    
      

  return (
    <div className='note'>NotePage
    <div className='note-header'><h3><ArrowLeft onClick={updateNote}></ArrowLeft></h3></div>
        <h1>Single Note{noteId}</h1>
        <textarea onChange={(e) => {setNotes({ ...note ,'body':e.target.value})}}defaultValue={note?.body}></textarea>
    </div>
  )
}

export default NotePage
